mapper.select:
select
from discuss_post where status != 2
and user_id = #{userId}
order by type desc, create_time desc
order by type desc, score desc, create_time desc
limit #{offset}, #{limit}

select
from discuss_post where status != 2
and section_id = #{sectionId}
order by type desc, create_time desc
order by type desc, score desc, create_time desc
limit #{offset}, #{limit}

mapper.update:
update discuss_post
set comment_count = #{commentCount}
where id = #{id}

update discuss_post
set type = #{type}
where id = #{postId}

update discuss_post
set status = #{status}
where id = #{postId}

update discuss_post
set score = #{score}
where id = #{postId}

update discuss_post
set views = #{views}
where id = #{postId}
